mapper.select:
select CM_KEY workKey,CM_ID cmId,(select CM_NAME from GZCRM_CMINFO where CM_ID = GZCRM_CMINFO.CM_ID ) cmName, CM_OFFICE_DATE workDate,CM_POSITION workPosition,CM_UNIT workUnit, CM_ATTACHMENT workAttachment,CM_WORK workWork,CM_MODIFICATION_DATE workModiDate, CM_MODIFICATION_PERSON workModiPerson from gzcrm_cmwork where 1=1
and CM_ID like '%'||#{cmId}||'%'
and CM_POSITION=#{workPosition}
and CM_KEY=#{workKey}

mapper.update:
update gzcrm_cmwork set CM_OFFICE_DATE=#{workDate},CM_POSITION=#{workPosition},CM_UNIT=#{workUnit},
CM_ATTACHMENT=#{workAttachment},CM_WORK=#{workWork},CM_MODIFICATION_DATE=#{workModiDate},
CM_MODIFICATION_PERSON=#{workModiPerson} where CM_KEY=#{workKey}
